import React, {useState} from 'react';

function Likes() {
    let [like1, setLike1] = useState(0);
    let [like2, setLike2] = useState(0);
    const colorArr = ['purple', 'blue', 'green', 'yellow', 'orange', 'red'];

    return (
        <div>
            <div>
                <h2>Did this place satisfy your munchies?</h2>
            </div>
            <button style={{ backgroundColor: colorArr[like1 % colorArr.length] }} onClick={() => setLike1(++like1)}>
                {like1} Yum yum!
            </button>
            <button style={{ backgroundColor: colorArr[like1 % colorArr.length] }} onClick={() => setLike2(++like2)}>
                {like2} Hell nah!
            </button>
        </div>
    );
}

export default Likes;
